sys_relationship.query_with:
(function refineQuery(current, parent) {

	// Add your code here, such as current.addQuery(field, value);

	//current.addQuery('user', parent.student);

var gdt = new GlideDateTime(parent.start_date_time);	
gt = gdt.getTime();
gd = gdt.getDate();
var day= gt.getByFormat('HHmmss');
var year= gd.getByFormat('yyyyMMdd');
var t='T';
var z='Z';
var result = year+t+day+z;

current.addQuery('start_date_time',result);
current.addQuery('user', parent.tutor).addOrCondition('user',parent.student); 
current.addQuery('name', 'Lesson');



})(current, parent);
